import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Orders = props => (
  <tr>
    <td>{props.orders.item}</td>
    <td>{props.orders.qty}</td>
    <td>{props.orders.date.substring(0,10)}</td>
    <td>
      <Link to={"/edit/"+props.orders._id}>edit</Link> | <a href="#" onClick={() => { props.deleteOrders(props.orders._id) }}>delete</a>
    </td>
  </tr>
)


export default class Order extends Component {
  constructor(props) {
    super(props);

    this.state = {
      orders: []
    }
  }

  deleteOrders(id) {
    axios.delete('http://localhost:5000/items/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      orders: this.state.orders.filter(el => el._id !== id)
    })
  }

    componentDidMount() {
      axios.get('http://localhost:5000/items/')
        .then(response => {
          console.log('databse is returning', response.data)
          if (response.data.length > 0) {
            this.setState({
              orders: response.data,
            })
          }
          // document.getElementById('vieworders').appendChild(
          // this.state.orders.map(function(orders) {
          //   return <div>
          //         <p>
          //          ${orders.item}
          //          ${orders.qty}
          //         </p>
          //     </div>
          //   })
          // )
        })
        .catch((error) => {
          console.log(error)
        })
  
    }
    orderList(){
        return this.state.orders.map(currentOrders => {
          return <Orders orders={currentOrders} deleteOrders={this.deleteOrder} keys=
          {currentOrders._id}/>;
        })
    }

  render() {
    return (
      <div class="contain">
          <div id="brucecont">
            <div id="bruce">
              Bruce's Diner
            </div>
          </div>
            <div id="nav">
              <ul>
              <li id="left"><a href="/orderList(){
                  return this.state.item 
              }">Order Form</a></li>
                <li id="mid"><a href="/view">View Orders</a></li>
                <li id="right"><a href="/landing">Log Out</a></li>
              </ul>
            </div>
            <div id='vieworders'>
              {this.orderList()}
            </div>
      </div>
    );
  }
}
