import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class Order extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: ''
    }
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const user = {
      username: this.state.username
    }

    console.log(user);

    axios.post('http://localhost:5000/users/add', user)
      .then(res => console.log(res.data));

    this.setState({
      username: ''
    })
  }

  render() {
    return (
      <div>
          <div id="nam">
              Bruce's Diner
          </div>
          <div id="Nav">
             Nav goes here
          </div>
          <div>
              Id Like to order
          </div>
          <div>
          <select id="meals">
            <option value="one">Dinner for 1</option>
            <option value="two">Dinner for 2</option>
            <option value="three">Dinner for 3</option>
            <option value="four">Dinner for 4</option>
          </select>
          </div>
          <div>
              QTY & Order button
          </div>
      </div>
    );
  }
}
